"use strict";var express=require("express"),posts=require("./data/db"),server=express();server.use(express.json()),server.get("/",function(s,e){return e.send("API up and running!")}),server.post("/api/posts",function(s,e){if(!s.body)return e.status(400).json({message:"Missing title or contents"});posts.insert(s.body).then(function(s){e.status(201).json(s)}).catch(function(s){console.log(s),e.status(500).json({message:"Error adding the post"})})}),server.listen(8e3,function(){return console.log("API running on port 8000")});